mapper.delete:
update yzzb_duty_zb zb
  set state=0,update_time=#{updateDate}
where zb.zb_date >= #{startDate}
  and zb.zb_date < adddate(#{endDate},interval 1 day)

mapper.update:
update yzzb_duty_zb
set publish_state=1,update_time=#{updateDate}
where state=1
  and zb_date >= #{startDate}
  and zb_date < adddate(#{endDate},interval 1 day)
