mapper.insert:
insert into yzzb_duty_history (index_week, `date`, employee_a, 
  employee_b, employee_c, `state`, 
  publish_time, create_time, update_time
  )
values (#{indexWeek,jdbcType=INTEGER}, #{date,jdbcType=DATE}, #{employeeA,jdbcType=INTEGER}, 
  #{employeeB,jdbcType=INTEGER}, #{employeeC,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, 
  #{publishTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(id)
from yzzb_duty_history
where state='1'
      and date >= #{start}
      and date <= #{end}
